mapper.insert:
INSERT INTO INFO_BOARD(
    BOARD_NUM
    , INFO_TITLE
    , INFO_CONTENT
)VALUES (
    (SELECT NVL(MAX(BOARD_NUM),0)+1 FROM INFO_BOARD)
    , #{infoTitle}
    ,#{infoContent}
)

mapper.update:
UPDATE INFO_BOARD
SET
    INFO_TITLE = #{infoTitle}
    , INFO_CONTENT= #{infoContent}
WHERE BOARD_NUM= #{boardNum}
