mapper.insert:
INSERT INTO MEMBER_INQUIRY_WRITE (
    MEMBER_INQUIRY_WRITE_NO
    , MEMBER_INQUIRY_CODE
    , MEMBER_INQUIRY_TITLE
    , MEMBER_INQUIRY_DETAIL
    , MEMBER_WRITE_DATE
    , USER_NO
)
VALUES (
    (SELECT 'INQ_WR_'||LPAD(COUNT(*) + 1, 3, '0')
    FROM MEMBER_INQUIRY_WRITE)
    , #{memberInquiryCode}
    , #{memberInquiryTitle}
    , #{memberInquiryDetail}
    , #{memberWriteDate}
    , #{userNo}
);

INSERT INTO MEMBER_INQUIRY_IMG (
    MEMBER_INQUIRY_IMG_NO
    , MEMBER_INQUIRY_WRITE_NO
    , MEMBER_INQUIRY_IMG_NAME
    , MEMBER_ATTACHED_INQUIRY_IMG_NAME
)
VALUES (
    (SELECT 'INQ_IMG_' || LPAD(COUNT(*) + 1, 3, '0')
    FROM MEMBER_INQUIRY_IMG)
    , #{memberInquiryImgNo}
    , #{memberInquiryWriteNo}
    , #{memberInquiryImgName}
    , #{memberAttachedInquiryImgName}
);

INSERT INTO MEMBER_INQUIRY_TYPE (
    MEMBER_INQUIRY_CODE
    , MEMBER_INQUIRY_NAME
)
VALUES (
    #{memberInquiryCode}
    , #{memberInquiryName}
);
