mapper.insert:
INSERT INTO MEMBER_USER (
    USER_NO
    , USER_NAME
    , PASSWORD
    , LOGIN_TYPE
    , NICK_NAME
) VALUES (
    (SELECT NVL(MAX(USER_NO)+1, 0)
    FROM MEMBER_USER)
    , #{userName}
    , #{passWord}
    , #{loginType}
    , #{nickName}
)

mapper.update:
UPDATE FROM MEMBER_USER
INNER JOIN MEMBER_IMG MI
ON USER_NO = MI.USER_NO
SET PROFILE_IMG_NAME = #{profile_img_name}
    , ATTACHED_PROFILE_IMG_NAME = #{attached_profile_img_name}
WHERE PROFILE_IMG_NO = #{profile_img_no}

mapper.select:
SELECT USER_NAME
FROM MEMBER_USER
WHERE USER_NAME = #{userName}

mapper.update:
UPDATE FROM MEMBER_USER
SET PASSWORD = #{passWord}
WHERE USER_NAME = #{userName}
