mapper.insert:
INSERT INTO MEMBER_USER (
    USER_NO
    , USER_NAME
    , PASSWORD
    , LOGIN_TYPE
    , NICK_NAME
) VALUES (
    (SELECT 'USER_' || LPAD(NVL(MAX(TO_NUMBER(SUBSTR(USER_NO, 6))), 0) + 1, 3, '0')
    FROM MEMBER_USER)
    , #{userName}
    , #{passWord}
    , #{loginType}
    , #{nickName}
)

INSERT INTO MEMBER_IMG (
    PROFILE_IMG_NO
    , PROFILE_IMG_NAME
    , ATTACHED_PROFILE_IMG_NAME
    , USER_NO
) VALUES (
    (SELECT 'PRO_IMG_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(PROFILE_IMG_NO, 9))), 0) + 1, 3, '0')
    FROM MEMBER_IMG)
    , #{profileImgName}
    , #{attachedProfileImgName}
    , #{userNo}
)

mapper.select:
SELECT USER_NAME
FROM MEMBER_USER
WHERE USER_NAME = #{userName}

mapper.update:
UPDATE FROM MEMBER_USER
SET PASSWORD = #{passWord}
WHERE USER_NAME = #{userName}
