mapper.insert:
INSERT INTO ALARM(
    ALARM_CODE
    , USER_NO
    , AUTHORITY_UPDATE
) VALUES (
    (SELECT 'ALARM_'||LPAD(NVL(MAX(TO_NUMBER(SUBSTR(ALARM_CODE, 7))), 0) + 1, 3, '0') ALARM_CODE
    FROM ALARM)
    , #{userNo}
    , #{authorityUpdate}
)

mapper.select:
SELECT AUTHORITY_UPDATE
FROM ALARM
WHERE USER_NO = #{userNo}
